TEAMWORK.resetDonation=function(){$("#amount").val("");$("#amount").focus()};$(function(){TEAMWORK.resetDonation();var a=StripeCheckout.configure({key:TEAMWORK.stripePK,locale:"auto",name:"Donate to TeamWork.io",description:'Charges will appear as "Banrai LLC"',token:function(b){$("input#stripeToken").val(b.id);$("form").submit()}});$("#amountCheck").on("click",function(c){c.preventDefault();var b=$("#amount").val();b=b.replace(/\$/g,"").replace(/\,/g,"");b=parseFloat(b);if(isNaN(b)){TEAMWORK.showModal("Sorry, bad amount","Please enter a valid amount","You will be charged in USD");TEAMWORK.resetDonation()}else{if(b<10){TEAMWORK.showModal("Sorry, insufficient amount","The minimum donation is $10","You will be charged in USD");TEAMWORK.resetDonation()}else{b=b*100;a.open({amount:Math.round(b)})}}})});